pragma solidity ^0.8.24;

import {AMMStrategyBase} from "./AMMStrategyBase.sol";
import {IAMMStrategy, TradeInfo} from "./IAMMStrategy.sol";

contract Strategy is AMMStrategyBase {
    // slots:
    // 0 lastTimestamp
    // 1 currentBidFee
    // 2 currentAskFee
    // 3 fairPrice (WAD, Y per X)
    // 4 mode (0 normal, 1 defensive)

    uint256 private constant TIGHT_BAND_BPS = 25;
    uint256 private constant TIGHT_FEE_BPS = 28;

    uint256 private constant BASE_UNDERCUT_BPS = 11;
    uint256 private constant BASE_BUFFER_BPS = 2;

    uint256 private constant ENTER_DEF_BPS = 80;
    uint256 private constant EXIT_DEF_BPS = 50;
    uint256 private constant DEF_UNDERCUT_DELTA_BPS = 2;
    uint256 private constant DEF_BUFFER_ADD_BPS = 0;
    uint256 private constant DEF_TIGHT_FEE_BPS = 29;

    function afterInitialize(uint256 initialX, uint256 initialY)
        external
        override
        returns (uint256 bidFee, uint256 askFee)
    {
        uint256 p0 = initialX == 0 ? WAD : wdiv(initialY, initialX);
        slots[0] = type(uint256).max;
        slots[3] = p0;
        slots[4] = 0;

        bidFee = bpsToWad(25);
        askFee = bpsToWad(25);
        slots[1] = bidFee;
        slots[2] = askFee;
    }

    function afterSwap(TradeInfo calldata trade)
        external
        override
        returns (uint256 bidFee, uint256 askFee)
    {
        uint256 lastTs = slots[0];
        uint256 prevBid = slots[1];
        uint256 prevAsk = slots[2];
        uint256 fair = slots[3];
        uint256 mode = slots[4];

        uint256 rx = trade.reserveX;
        uint256 ry = trade.reserveY;
        uint256 spot = rx == 0 ? 0 : wdiv(ry, rx);

        if (trade.timestamp != lastTs) {
            uint256 gamma = trade.isBuy ? (WAD - prevBid) : (WAD - prevAsk);
            uint256 fairCandidate = fair;
            if (gamma != 0 && spot != 0) {
                fairCandidate = trade.isBuy ? wmul(spot, gamma) : wdiv(spot, gamma);
            }

            uint256 maxJump = 400 * BPS; // 4%
            if (fair != 0) {
                uint256 rel = wdiv(absDiff(fairCandidate, fair), fair);
                if (rel > maxJump) {
                    uint256 delta = wmul(fair, maxJump);
                    fairCandidate = fairCandidate > fair ? fair + delta : fair - delta;
                }
            }

            fair = (fair * 80 + fairCandidate * 20) / 100;
            slots[0] = trade.timestamp;
            slots[3] = fair;
        }

        if (spot == 0 || fair == 0) {
            bidFee = bpsToWad(30);
            askFee = bpsToWad(30);
            slots[1] = bidFee;
            slots[2] = askFee;
            slots[4] = mode;
            return (bidFee, askFee);
        }

        uint256 mis = wdiv(absDiff(spot, fair), fair);

        if (mode == 0) {
            if (mis > bpsToWad(ENTER_DEF_BPS)) mode = 1;
        } else {
            if (mis < bpsToWad(EXIT_DEF_BPS)) mode = 0;
        }

        uint256 undercutBps = BASE_UNDERCUT_BPS;
        uint256 bufferBps = BASE_BUFFER_BPS;
        uint256 tightFeeBps = TIGHT_FEE_BPS;
        if (mode == 1) {
            undercutBps = undercutBps > DEF_UNDERCUT_DELTA_BPS ? undercutBps - DEF_UNDERCUT_DELTA_BPS : 0;
            bufferBps += DEF_BUFFER_ADD_BPS;
            if (DEF_TIGHT_FEE_BPS > tightFeeBps) tightFeeBps = DEF_TIGHT_FEE_BPS;
        }

        if (mis <= bpsToWad(TIGHT_BAND_BPS)) {
            bidFee = bpsToWad(tightFeeBps);
            askFee = bpsToWad(tightFeeBps);
        } else {
            uint256 gammaBase = WAD - bpsToWad(30);
            uint256 gammaBaseSq = wmul(gammaBase, gammaBase);
            uint256 undercut = bpsToWad(undercutBps);
            uint256 buffer = bpsToWad(bufferBps);

            if (spot > fair) {
                uint256 gammaReq = wdiv(fair, spot);
                uint256 req = gammaReq >= WAD ? 0 : (WAD - gammaReq);
                bidFee = clampFee(req + buffer);

                uint256 gammaMatch = wdiv(wmul(spot, gammaBaseSq), fair);
                uint256 askRaw = gammaMatch >= WAD ? 0 : (WAD - gammaMatch);
                askFee = askRaw > undercut ? askRaw - undercut : 0;
            } else {
                uint256 gammaReq = wdiv(spot, fair);
                uint256 req = gammaReq >= WAD ? 0 : (WAD - gammaReq);
                askFee = clampFee(req + buffer);

                uint256 gammaMatch = wdiv(wmul(fair, gammaBaseSq), spot);
                uint256 bidRaw = gammaMatch >= WAD ? 0 : (WAD - gammaMatch);
                bidFee = bidRaw > undercut ? bidRaw - undercut : 0;
            }
        }

        bidFee = clampFee(bidFee);
        askFee = clampFee(askFee);
        slots[1] = bidFee;
        slots[2] = askFee;
        slots[4] = mode;
    }

    function getName() external pure override returns (string memory) {
        return "gate2r_v10";
    }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import {AMMStrategyBase} from "./AMMStrategyBase.sol";
import {IAMMStrategy, TradeInfo} from "./IAMMStrategy.sol";

contract Strategy is AMMStrategyBase {
    // slots:
    // 0 lastTimestamp
    // 1 currentBidFee
    // 2 currentAskFee
    // 3 fairPrice (WAD)
    // 4 mode (0 normal, 1 defensive, 2 recovery)
    // 5 cooldown (steps remaining)
    // 6 initialX

    uint256 private constant ENTER_MIS_BPS = __ENTER_MIS_BPS__;
    uint256 private constant EXIT_MIS_BPS = __EXIT_MIS_BPS__;
    uint256 private constant NORMAL_MIS_BPS = __NORMAL_MIS_BPS__;

    uint256 private constant ENTER_INV_BPS = __ENTER_INV_BPS__;
    uint256 private constant EXIT_INV_BPS = __EXIT_INV_BPS__;
    uint256 private constant NORMAL_INV_BPS = __NORMAL_INV_BPS__;

    uint256 private constant DEF_COOLDOWN = __DEF_COOLDOWN__;
    uint256 private constant REC_COOLDOWN = __REC_COOLDOWN__;

    uint256 private constant TIGHT_BAND_BPS = __TIGHT_BAND_BPS__;
    uint256 private constant NORM_TIGHT_BPS = __NORM_TIGHT_BPS__;
    uint256 private constant REC_TIGHT_BPS = __REC_TIGHT_BPS__;
    uint256 private constant DEF_TIGHT_BPS = __DEF_TIGHT_BPS__;

    uint256 private constant NORM_BUF_BPS = __NORM_BUF_BPS__;
    uint256 private constant REC_BUF_BPS = __REC_BUF_BPS__;
    uint256 private constant DEF_BUF_BPS = __DEF_BUF_BPS__;

    uint256 private constant NORM_UNDER_BPS = __NORM_UNDER_BPS__;
    uint256 private constant REC_UNDER_BPS = __REC_UNDER_BPS__;
    uint256 private constant DEF_UNDER_BPS = __DEF_UNDER_BPS__;

    uint256 private constant TILT_TRIGGER_BPS = __TILT_TRIGGER_BPS__;
    uint256 private constant TILT_NORM_BPS = __TILT_NORM_BPS__;
    uint256 private constant TILT_DEF_BPS = __TILT_DEF_BPS__;

    function afterInitialize(uint256 initialX, uint256 initialY)
        external
        override
        returns (uint256 bidFee, uint256 askFee)
    {
        uint256 p0 = initialX == 0 ? WAD : wdiv(initialY, initialX);
        slots[0] = type(uint256).max;
        slots[3] = p0;
        slots[4] = 0;
        slots[5] = 0;
        slots[6] = initialX;

        bidFee = bpsToWad(NORM_TIGHT_BPS);
        askFee = bpsToWad(NORM_TIGHT_BPS);
        slots[1] = bidFee;
        slots[2] = askFee;
    }

    function afterSwap(TradeInfo calldata trade)
        external
        override
        returns (uint256 bidFee, uint256 askFee)
    {
        uint256 lastTs = slots[0];
        uint256 prevBid = slots[1];
        uint256 prevAsk = slots[2];
        uint256 fair = slots[3];
        uint256 mode = slots[4];
        uint256 cooldown = slots[5];
        uint256 initX = slots[6];

        uint256 rx = trade.reserveX;
        uint256 ry = trade.reserveY;
        uint256 spot = rx == 0 ? 0 : wdiv(ry, rx);

        bool newStep = trade.timestamp != lastTs;
        uint256 dt = 0;

        if (newStep) {
            dt = 1;
            if (lastTs != type(uint256).max && trade.timestamp > lastTs) {
                dt = trade.timestamp - lastTs;
            }
            if (dt > 50) dt = 50;

            if (cooldown > dt) cooldown -= dt;
            else cooldown = 0;

            uint256 gammaUsed = trade.isBuy ? (WAD - prevBid) : (WAD - prevAsk);
            uint256 fairCandidate = fair;
            if (gammaUsed != 0 && spot != 0) {
                fairCandidate = trade.isBuy ? wmul(spot, gammaUsed) : wdiv(spot, gammaUsed);
            }

            uint256 maxJump = (320 + 4 * dt) * BPS;
            if (maxJump > 500 * BPS) maxJump = 500 * BPS;
            if (fair != 0) {
                uint256 rel = wdiv(absDiff(fairCandidate, fair), fair);
                if (rel > maxJump) {
                    uint256 delta = wmul(fair, maxJump);
                    fairCandidate = fairCandidate > fair ? fair + delta : fair - delta;
                }
            }

            fair = (fair * 80 + fairCandidate * 20) / 100;
            slots[0] = trade.timestamp;
            slots[3] = fair;
        }

        if (spot == 0 || fair == 0) {
            bidFee = bpsToWad(30);
            askFee = bpsToWad(30);
            slots[1] = bidFee;
            slots[2] = askFee;
            slots[4] = mode;
            slots[5] = cooldown;
            return (bidFee, askFee);
        }

        uint256 mis = wdiv(absDiff(spot, fair), fair);

        uint256 inv = 0;
        if (initX != 0) {
            inv = wdiv(absDiff(rx, initX), initX);
        }

        bool stress = mis > bpsToWad(ENTER_MIS_BPS) || inv > bpsToWad(ENTER_INV_BPS);
        bool calm = mis < bpsToWad(EXIT_MIS_BPS) && inv < bpsToWad(EXIT_INV_BPS);

        if (mode == 0) {
            if (stress) {
                mode = 1;
                cooldown = DEF_COOLDOWN;
            }
        } else if (mode == 1) {
            if (stress) {
                cooldown = DEF_COOLDOWN;
            } else if (calm && cooldown == 0) {
                mode = 2;
                cooldown = REC_COOLDOWN;
            }
        } else {
            if (stress) {
                mode = 1;
                cooldown = DEF_COOLDOWN;
            } else if (
                cooldown == 0
                    && mis < bpsToWad(NORMAL_MIS_BPS)
                    && inv < bpsToWad(NORMAL_INV_BPS)
            ) {
                mode = 0;
            }
        }

        uint256 tightFeeBps = mode == 1
            ? DEF_TIGHT_BPS
            : (mode == 2 ? REC_TIGHT_BPS : NORM_TIGHT_BPS);
        if (newStep && dt > 2 && tightFeeBps > 24) {
            tightFeeBps -= 1;
        }

        if (mis <= bpsToWad(TIGHT_BAND_BPS)) {
            bidFee = bpsToWad(tightFeeBps);
            askFee = bpsToWad(tightFeeBps);
        } else {
            uint256 gammaBase = WAD - bpsToWad(30);
            uint256 gammaBaseSq = wmul(gammaBase, gammaBase);

            uint256 bufferBps = mode == 1 ? DEF_BUF_BPS : (mode == 2 ? REC_BUF_BPS : NORM_BUF_BPS);
            uint256 undercutBps =
                mode == 1 ? DEF_UNDER_BPS : (mode == 2 ? REC_UNDER_BPS : NORM_UNDER_BPS);

            uint256 buffer = bpsToWad(bufferBps);
            uint256 undercut = bpsToWad(undercutBps);

            if (spot > fair) {
                uint256 gammaReq = wdiv(fair, spot);
                uint256 req = gammaReq >= WAD ? 0 : (WAD - gammaReq);
                bidFee = clampFee(req + buffer);

                uint256 gammaMatch = wdiv(wmul(spot, gammaBaseSq), fair);
                uint256 askRaw = gammaMatch >= WAD ? 0 : (WAD - gammaMatch);
                askFee = askRaw > undercut ? (askRaw - undercut) : 0;
            } else {
                uint256 gammaReq = wdiv(spot, fair);
                uint256 req = gammaReq >= WAD ? 0 : (WAD - gammaReq);
                askFee = clampFee(req + buffer);

                uint256 gammaMatch = wdiv(wmul(fair, gammaBaseSq), spot);
                uint256 bidRaw = gammaMatch >= WAD ? 0 : (WAD - gammaMatch);
                bidFee = bidRaw > undercut ? (bidRaw - undercut) : 0;
            }
        }

        if (initX != 0) {
            uint256 tiltBps = mode == 1 ? TILT_DEF_BPS : TILT_NORM_BPS;
            uint256 tilt = bpsToWad(tiltBps);
            uint256 tiltTrigger = bpsToWad(TILT_TRIGGER_BPS);

            if (rx > initX) {
                uint256 longX = wdiv(rx - initX, initX);
                if (longX > tiltTrigger) {
                    bidFee = clampFee(bidFee + tilt);
                    askFee = askFee > tilt ? (askFee - tilt) : 0;
                }
            } else {
                uint256 shortX = wdiv(initX - rx, initX);
                if (shortX > tiltTrigger) {
                    askFee = clampFee(askFee + tilt);
                    bidFee = bidFee > tilt ? (bidFee - tilt) : 0;
                }
            }
        }

        bidFee = clampFee(bidFee);
        askFee = clampFee(askFee);

        slots[1] = bidFee;
        slots[2] = askFee;
        slots[4] = mode;
        slots[5] = cooldown;
    }

    function getName() external pure override returns (string memory) {
        return "__STRATEGY_NAME__";
    }
}

